import React, { useEffect, useState } from "react";
import "./Banner.css";

const Banner = () => {
    const [movie, setMovie] = useState(null);

    useEffect(() => {
        // Fetch JSON from the 'public/data' folder
        fetch(process.env.PUBLIC_URL + "/data/movies.json")
            .then((response) => response.json())
            .then((data) => {
                const x = Math.floor(Math.random() * data.results.length);
                setMovie(data.results[x]); // Pick a random movie
            })
            .catch((error) => console.error("Error loading JSON:", error));
    }, []);

    return (
        <div
            style={{
                backgroundImage: `url(${movie ? movie.backdrop_path : ""})`,
            }}
            className="banner"
        >
            <div className="fade_content">
                <div className="content">
                    <h1 className="title">{movie ? movie.title || movie.name : "No Title"}</h1>
                    <div className="banner_buttons">
                        <button className="button">
                            <i className="fa fa-play"></i>Play
                        </button>
                        <button className="button">
                            <i className="fa fa-bars"></i>My List
                        </button>
                    </div>
                    <h1 className="description">{movie ? movie.overview : "No Description"}</h1>
                </div>
            </div>
            <div className="fade-bottom"></div>
        </div>
    );
};

export default Banner;
